    return (
      <div>
        {results.map((question, index) => (
          <div key={index}>
            <h2>Question {index + 1}</h2>
            <p>{question.question}</p>
            <p>Correct Answer: {question.correct_answer}</p>
          </div>
        ))}
      </div>
    );
  }

  render() {
    const { results } = props;
    // console.log(this.props);
