import React, { Component } from "react";
// import QuizParams from "./QuizParams";
import "../App.css";

// send variable from Categories, and do apiKeys[cat].name...etc

class Quiz extends Component {
  constructor() {
    super();
    this.state = {};
    this.putQuestion = this.putQuestion.bind(this);
    this.createAnswers = this.createAnswers.bind(this);
  }
  // results is undefined
  render() {
    console.log(this.props.data);
    const questionData = this.props.data;
    console.log(questionData);
    let counter = 0;
    // console.log(this.props);
    return (
      <div>
        <h1>K.</h1>
        <div>
          <div>{this.putQuestion}</div>
          <form>
            {this.createAnswers}
            <input
              type="submit"
              value={questionData[0].incorrect_answers[0]}
              className="inputQuiz"
            />
          </form>
        </div>
      </div>
    );
  }

  createAnswers() {
    const inputElements = Array.from({ length: 4 }, (_, index) => {
      const input = document.createElement("input");
      input.type = "text";
      input.className = "inputQuiz";
      input.value = `Input ${index + 1}`;
      return input;
    });

    // Shuffle the array
    inputElements.sort(() => Math.random() - 0.5);

    // Append input elements to the body
    inputElements.forEach((input) => {
      document.body.appendChild(input);
    });
  }
  putQuestion() {
    this.questionData.forEach((question) => {
      console.log(question.category);
    });
  }
}
export default Quiz;
